    #region methods

    /// <summary>
    /// Pans the map to NewYork city
    /// </summary>
    private void PanToNY()
    {
        _map.PanTo(new PointF(40.713185f, -74.0072333f), animate: true, duration: 10f);
    }

    /// <summary>
    /// Restricts the map view to the given bounds.
    /// </summary>
    private void SetMaxBounds()
    {
        _map.SetMaxBounds(new Tuple<LatLng, LatLng>(new LatLng(45.943f, 24.967f), new LatLng(46.943f, 25.967f)));
    }

    /// <summary>
    /// Disable max bounds
    /// </summary>
    private void DisableMaxBounds()
    {
        _map.SetMaxBounds(new Tuple<LatLng, LatLng>(null, null));
    }

    /// <summary>
    /// Add Search Control
    /// </summary>
    private void AddSearchControl()
    {
        _map.AddSearchControl();
    }

    /// <summary>
    /// Add Osrm routing control
    /// </summary>
    private void AddOsrmRoutingControl()
    {
        _map.AddOsrmRoutingControl(new RoutingControlOptions
        {
            Collapsible = true,
            Waypoints = new[] { new LatLng(48.117266f, -1.6777926f), new LatLng(47.218371f, -1.553621f) },
            Router = new Router { ServiceUrl = "https://router.project-osrm.org/route/v1", Profile = "driving" }
        });
    }

    /// <summary>
    /// Add MapBox routing control
    /// </summary>
    private void AddMapBoxRoutingControl()
    {
        _map.AddMapBoxRoutingControl("pk.eyJ1IjoiaWFkbGFuZSIsImEiOiJja3E5aWJwaHgwNzZnMnBxdGhodjZhZzd0In0.aJirrr0eAG16APO5KiwsAQ", new RoutingControlOptions
        {
            Collapsible = true,
            Waypoints = new[] { new LatLng(45.943f, 24.967f), new LatLng(46.943f, 25.967f) },
            Router = new Router { ServiceUrl = "https://api.mapbox.com/directions/v5", Profile = "mapbox/walking" }
        });
    }

    #endregion
}

/// <summary>
/// City
/// </summary>
public class City
{

    public string CoatOfArmsImageUrl { get; set; }

    public string Country { get; set; }

    public string Name { get; set; }

    public string Description { get; set; }

    public PointF Coordinates { get; set; }

}
